<script lang="ts">
import { defineComponent, onMounted, reactive, toRefs } from "vue";
import {
  IconDown,
  IconUp,
  IconEyeInvisible,
  IconEye,
} from "@arco-design/web-vue/es/icon";
interface stateValue {
  isEdit?: boolean;
  isReload?: boolean;
  isClose?: boolean;
  isStop?: boolean;
  isForword?: boolean;
  isRun?: boolean;
  isSpeed?: boolean;
  isTospeed?: boolean;
  isMore?: boolean;
  isMsg?: boolean;
  isEye?: boolean;
}
export default defineComponent({
  name: "HeadCenter",
  components: {
    IconDown,
    IconUp,
    IconEyeInvisible,
    IconEye,
  },
  props: ["isIconText"],
  setup(props, context) {
    const { isIconText } = toRefs(props);
    const state: stateValue = reactive({
      isEdit: false,
      isReload: false,
      isClose: false,
      isStop: false,
      isForword: false,
      isRun: false,
      isSpeed: false,
      isTospeed: false,
      isMore: false,
      isMsg: false,
      isEye: false,
    });
    //选择更多菜单的项目
    const chooseMore = () => {};
    //选择信息菜单的项目
    const chooseMsg = (value:any) => {
      if (value === "eyes") {
        state.isEye = !state.isEye;
      }
    };
    onMounted(() => {});
    return {
      ...toRefs(state),
      isIconText,
      chooseMore,
      chooseMsg,
    };
  },
});
</script>

